changeSet.createProcedure:
DROP TRIGGER IF EXISTS tgr_locationinfo_trash ON t_inodes;
-- -- store location of deleted inodes in trash table -- -- stores a old values into the trash table except last access time, -- which replaced with a time, when the trigger was running --
CREATE OR REPLACE FUNCTION f_locationinfo2trash() RETURNS TRIGGER AS $t_inodes_trash$ BEGIN
IF (TG_OP = 'DELETE') THEN
INSERT INTO t_locationinfo_trash SELECT ipnfsid , itype, ilocation , ipriority, ictime , iatime , istate FROM t_locationinfo WHERE ipnfsid = OLD.ipnfsid UNION SELECT OLD.ipnfsid, 2, '', 0, now(), now(), 1;
END IF;
RETURN OLD; END;
$t_inodes_trash$ LANGUAGE plpgsql;
-- -- trigger to store removed inodes --
CREATE TRIGGER tgr_locationinfo_trash BEFORE DELETE ON t_inodes FOR EACH ROW EXECUTE PROCEDURE f_locationinfo2trash();

rollback.createProcedure:
DROP TRIGGER IF EXISTS tgr_locationinfo_trash ON t_inodes;
-- -- store location of deleted inodes in trash table -- -- stores a old values into the trash table except last access time, -- which replaced with a time, when the trigger was running --
CREATE OR REPLACE FUNCTION f_locationinfo2trash() RETURNS TRIGGER AS $t_inodes_trash$ BEGIN
IF (TG_OP = 'DELETE') THEN
INSERT INTO t_locationinfo_trash SELECT ipnfsid , itype, ilocation , ipriority, ictime , iatime , istate FROM t_locationinfo WHERE ipnfsid = OLD.ipnfsid;
END IF;
RETURN OLD; END;
$t_inodes_trash$ LANGUAGE plpgsql;
-- -- trigger to store removed inodes --
CREATE TRIGGER tgr_locationinfo_trash BEFORE DELETE ON t_inodes FOR EACH ROW EXECUTE PROCEDURE f_locationinfo2trash();
